export class Iconlist extends Component{
  constructor(props) {
    super(props);
    this.state = {};

  }
  getSvg = (id)=>{
    return list[id];
  }
  render(){
    var width=this.props.width?this.props.width:50,height=this.props.height?this.props.height:50;
    //var className=['icons','icons1'];
    let icon;
    if(process.env.__CLIENT__ === true)
    {
      icon = list[this.props.iconName]||<span>X</span>
    }
    else {
      icon = <span>X</span>
    }
    let childProps ={
      onClick : this.props.onClick,
    }
    return (
      <i  {...childProps} className={this.props.className||'icons'} style={{width:width,height:height}}>
        {icon}
      </i>
    );
  }
}
